import React, { useState } from "react";
import { TextField } from "@mui/material";
import { Button } from "@material-ui/core"


export default function Cadastro(){

    const[nome,setNome]=useState('')
    const[cpf,setCpf]=useState('')
    const[rg,setRg]=useState('')
    const[nome_mae,setNomeMae]=useState('')
    const[data_de_cadastro,setDataCadastro]=useState('')
    const[data_de_nascimento,setDataNascimento]=useState('')

    const handleClick = (e) => {
        e.preventDefault()
        const usuario = {nome, cpf, rg, nome_mae, data_de_cadastro, data_de_nascimento}
        console.log(usuario)
        fetch("http://localhost:8080/usuario/add", {
            method:"POST",
            headers:{"Content-Type":"application/json"},
            body:JSON.stringify(usuario)
        }).then(()=>{
            console.log("Novo Usuario Cadastrado!")
        })
    }

    return(
        <div className="w-96 bg-white border border-gray-200 rounded-md mb-6 mx-16 shadow-lg max-w-sm text-center">
            <div className="bg-cyan-500 text-xl text-white py-4 font-semibold">
                Cadastrar Usuário
            </div>
            <div className="w-full pt-6 px-8">
                <form> 
                    <div className="mb-4">                
                        <TextField  id="outlined-basic" label="Nome" variant="outlined" fullWidth value={nome} onChange={(e)=>setNome(e.target.value)} />
                    </div>
                    <div className="mb-4">
                        <TextField id="outlined-basic" label="CPF"variant="outlined" fullWidth  value={cpf} onChange={(e)=>setCpf(e.target.value)}/>
                    </div>
                    <div className="mb-4">  
                        <TextField id="outlined-basic" label="RG" variant="outlined" fullWidth value={rg} onChange={(e)=>setRg(e.target.value)}/>
                    </div>
                    <div className="mb-4">               
                        <TextField id="outlined-basic" label="Nome da Mãe"variant="outlined" fullWidth value={nome_mae} onChange={(e)=>setNomeMae(e.target.value)}/>
                    </div>
                    <div className="mb-4"> 
                        <TextField id="outlined-basic" label="Data de Cadastro"variant="outlined" fullWidth value={data_de_cadastro} onChange={(e)=>setDataCadastro(e.target.value)} /> 
                    </div>
                    <div className="mb-8">
                        <TextField id="outlined-basic" label="Data de Nascimento" variant="outlined" fullWidth value={data_de_nascimento} onChange={(e)=>setDataNascimento(e.target.value)} /> 
                    </div>
                    <div className="mb-12"> 
                        <Button variant="contained" color="primary" onClick={handleClick}> Cadastrar </Button>
                    </div>
                </form>
            </div>
        </div>

    )
}
