import React, { useState } from "react";
import { TextField } from "@mui/material";
import * as yup from 'yup'
import { setLocale } from 'yup';


export default function Cadastro(){

    const[nome,setNome]=useState('')
    const[cpf,setCpf]=useState('')
    const[rg,setRg]=useState('')
    const[nome_mae,setNomeMae]=useState('')
    const[data_de_cadastro,setDataCadastro]=useState('')
    const[data_de_nascimento,setDataNascimento]=useState('')

    const handleClick = async (e) => {
        e.preventDefault()
        var usuario = {nome, cpf, rg, nome_mae, data_de_cadastro, data_de_nascimento}

        setLocale({
            // use constant translation keys for messages without values
            mixed: {
              default: 'field_invalid',
            },
            // use functions to generate an error object that includes the value from the schema
            number: {
              min: ({ min }) => ({ key: 'field_too_short', values: { min } }),
              max: ({ max }) => ({ key: 'field_too_big', values: { max } }),
            },
          });

        var userSchema = yup.object().shape({
            nome: yup.string().required(),
            nome_mae: yup.string().required(),
            cpf: yup.number().min(11).max(11).required() ,
            rg: yup.number().min(11).max(11).required() ,
            data_de_nascimento: yup.date().max(new Date(), 'Não é possível botar uma data futura').required(),
            data_de_cadastro: yup.date().max(new Date(), 'Não é possível botar uma data futura').required()
        });
        console.log(usuario)
/*         fetch("http://localhost:8080/usuario/add", {
            method:"POST",
            headers:{"Content-Type":"application/json"},
            body:JSON.stringify(usuario)
        }).then(()=>{
            alert("Novo Usuario Cadastrado!")
        }) */
    }

    return(
        <div className="flex justify-center">
            <div className="w-96 bg-white border border-gray-200 rounded-md mb-6 shadow-lg max-w-sm text-center">
                <div className="bg-cyan-500 text-xl text-white py-4 font-semibold">
                    Cadastrar Usuário
                </div>
                <div className="w-full pt-6 px-8">
                    <form> 
                        <div className="mb-4">                
                            <TextField  id="outlined-basic" label="Nome" variant="outlined" fullWidth value={nome} onChange={(e)=>setNome(e.target.value)} />
                        </div>
                        <div className="mb-4">
                            <TextField id="outlined-basic" label="CPF"variant="outlined" fullWidth  value={cpf} onChange={(e)=>setCpf(e.target.value)}/>
                        </div>
                        <div className="mb-4">  
                            <TextField id="outlined-basic" label="RG" variant="outlined" fullWidth value={rg} onChange={(e)=>setRg(e.target.value)}/>
                        </div>
                        <div className="mb-4">               
                            <TextField id="outlined-basic" label="Nome da Mãe"variant="outlined" fullWidth value={nome_mae} onChange={(e)=>setNomeMae(e.target.value)}/>
                        </div>
                        <div className="mb-4"> 
                            <TextField id="outlined-basic" label="Data de Cadastro"variant="outlined" fullWidth value={data_de_cadastro} onChange={(e)=>setDataCadastro(e.target.value)} /> 
                        </div>
                        <div className="mb-8">
                            <TextField id="outlined-basic" label="Data de Nascimento" variant="outlined" fullWidth value={data_de_nascimento} onChange={(e)=>setDataNascimento(e.target.value)} /> 
                        </div>
                        <div className="mb-12"> 
                            <button className="px-3 py-2 bg-cyan-700 hover:bg-cyan-900 rounded-md text-white text-md mr-2 font-se" onClick={handleClick}> Cadastrar </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    )
}
