import React, { useState } from "react";
import { TextField } from "@mui/material";
import * as yup from 'yup'
import { setLocale } from 'yup';
import { useFormik } from "formik";
import {useForm, Controller, set} from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup'


var userSchema = yup.object().shape({
    nome: yup.string().required("O campo é obrigatório"),
    nome_mae: yup.string().required("O campo é obrigatório"),
    cpf: yup.number().min(11, "O campo CPF deve ter 11 caracteres").max(11,"O campo CPF só pode ter 11 caracteres").required("O campo é obrigatório") ,
    rg: yup.number().min(11, "O campo RG deve ter 11 caracteres").max(11, 'O campo RG só pode ter 11 caracteres').required("O campo é obrigatório") ,
    data_de_nascimento: yup.date().required("O campo é obrigatório"),
    data_de_cadastro: yup.date().required("O campo é obrigatório")
});


export default function Cadastro(){

    const {control, handleSubmit, handleReg, formState:{ errors }} = useForm({
        resolver: yupResolver(userSchema)
    })

    const[nome,setNome]=useState('')
    const[cpf,setCpf]=useState('')
    const[rg,setRg]=useState('')
    const[nome_mae,setNomeMae]=useState('')
    const[data_de_cadastro,setDataCadastro]=useState('')
    const[data_de_nascimento,setDataNascimento]=useState('')

    const handleClick = async (e) => {
        e.preventDefault()
        var usuario = {nome, cpf, rg, nome_mae, data_de_cadastro, data_de_nascimento}

        console.log(usuario)
/*         fetch("http://localhost:8080/usuario/add", {
            method:"POST",
            headers:{"Content-Type":"application/json"},
            body:JSON.stringify(usuario)
        }).then(()=>{
            alert("Novo Usuario Cadastrado!")
        }) */
    }

    return(
        <div className="flex justify-center">
            <div className="w-96 bg-white border border-gray-200 rounded-md mb-6 shadow-lg max-w-sm text-center">
                <div className="bg-cyan-500 text-xl text-white py-4 font-semibold">
                    Cadastrar Usuário
                </div>
                <div className="w-full pt-6 px-8">
                    <form> 
                        <div className="mb-4"> 
                            <Controller
                            control={control}
                            name="nome"
                            render={({field: { onChange, onBlur, value}})=>( 
                                <TextField  
                                    id="outlined-basic" 
                                    label="Nome" 
                                    variant="outlined" 
                                    fullWidth value={nome} 
                                    autoCorrect={false}
                                    onChange={(e)=>setNome(e.target.value)} />
                            )}
                            />   
                            {errors.nome && <div className="text-red-500">{errors.nome?.message}</div>}
                        </div>

                        <div className="mb-4">
                            <TextField 
                                id="outlined-basic" 
                                label="CPF"variant="outlined" fullWidth  value={cpf} onChange={(e)=>setCpf(e.target.value)}/>
                        </div>
                        <div className="mb-4">  
                            <TextField id="outlined-basic" label="RG" variant="outlined" fullWidth value={rg} onChange={(e)=>setRg(e.target.value)}/>
                        </div>
                        <div className="mb-4">               
                            <TextField id="outlined-basic" label="Nome da Mãe"variant="outlined" fullWidth value={nome_mae} onChange={(e)=>setNomeMae(e.target.value)}/>
                        </div>
                        <div className="mb-4"> 
                            <TextField id="outlined-basic" label="Data de Cadastro"variant="outlined" fullWidth value={data_de_cadastro} onChange={(e)=>setDataCadastro(e.target.value)} /> 
                        </div>
                        <div className="mb-8">
                            <TextField id="outlined-basic" label="Data de Nascimento" variant="outlined" fullWidth value={data_de_nascimento} onChange={(e)=>setDataNascimento(e.target.value)} /> 
                        </div>
                        <div className="mb-12"> 
                            <button className="px-3 py-2 bg-cyan-700 hover:bg-cyan-900 rounded-md text-white text-md mr-2 font-se" onClick={handleClick}> Cadastrar </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    )
}
