import React from 'react'
import { Pagination } from '@mui/material';

export const Paginacao = ( {postPerPage, totalPosts, paginate} ) => {

    const numeroPaginas = [];

    for(let i=1 ; i<= Math.ceil(totalPosts/postPerPage); i++){
        numeroPaginas.push(i)

    }

  return (
   <nav className='bg-gray-100'>
        <ul className='flex justify-between mx-64 pt-2'>
            {numeroPaginas.map(numero=>(
                <li key={numero} className="bg-cyan-400 px-3 py-1 rounded-full font-normal border">
                    <a onClick={() => paginate(numero)} href='!#' className='page-link'>
                        {numero}
                    </a>
                </li>
            ))}
        </ul>
   </nav>
  )
}
