import React, { useState } from "react";
import { TextField } from "@mui/material";
import * as yup from 'yup'
import {useForm} from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup'
import { style } from "@mui/system";
import InputMask from 'react-input-mask';


var userSchema = yup.object().shape({
    nome: yup.string().required("O campo é obrigatório"),
    nome_mae: yup.string().required("O campo é obrigatório"),
    cpf: yup.number().min(11, "O campo CPF deve ter 11 caracteres").max(11,"O campo CPF só pode ter 11 caracteres").required("O campo é obrigatório") ,
    rg: yup.number().min(11, "O campo RG deve ter 11 caracteres").max(11, 'O campo RG só pode ter 11 caracteres').required("O campo é obrigatório") ,
    data_de_nascimento: yup.date(new Date,'A data deve ser válida').required("O campo é obrigatório"),
    data_de_cadastro: yup.date().required("O campo é obrigatório")
});


export default function Cadastro(){

    const { register, handleSubmit,  formState:{ errors } } = useForm({
        resolver: yupResolver(userSchema),
      });


    const[nome,setNome]=useState('')
    const[cpf,setCpf]=useState('')
    const[rg,setRg]=useState('')
    const[nome_mae,setNomeMae]=useState('')
    const[data_de_cadastro,setDataCadastro]=useState('')
    const[data_de_nascimento,setDataNascimento]=useState('')

    const handleClick = async (e) => {
        e.preventDefault()
        var usuario = {nome, cpf, rg, nome_mae, data_de_cadastro, data_de_nascimento}

        console.log(usuario)
/*         fetch("http://localhost:8080/usuario/add", {
            method:"POST",
            headers:{"Content-Type":"application/json"},
            body:JSON.stringify(usuario)
        }).then(()=>{
            alert("Novo Usuario Cadastrado!")
        }) */
    }

    return(
        <div className="flex justify-center">
            <div className="w-96 bg-white border border-gray-200 rounded-md mb-6 shadow-lg max-w-sm text-center">
                <div className="bg-cyan-500 text-xl text-white py-4 font-semibold">
                    Cadastrar Usuário
                </div>
                <div className="w-full pt-6 px-8">
                    <form> 
                        <div className="mb-4">
                            <label class="uppercase tracking-wide text-gray-700 text-xs font-bold mb-2 flex justify-start" for="grid-first-name">
                                Nome
                            </label>
                            <InputMask {...register('nome')} className="appearance-none block w-full bg-gray-100 text-gray-700 border border-cyan-500 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white" id="nome"  label="Nome" variant="outlined" fullWidth value={nome} onChange={(e)=>setNome(e.target.value)} />
                            {errors.nome?.message && <p>{errors.nome?.message}</p>}
                        </div>

                        <div className="mb-4">
                            <label class="uppercase tracking-wide text-gray-700 text-xs font-bold mb-2 flex justify-start" for="grid-first-name">
                                CPF
                            </label>
                            <input type="text"  className="appearance-none block w-full bg-gray-100 text-gray-700 border border-cyan-500 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white" {...register('cpf')}  id="outlined-basic" label="CPF"variant="outlined" fullWidth  value={cpf} onChange={(e)=>setCpf(e.target.value)}/>
                            {errors.cpf?.message && <p>{errors.cpf?.message}</p>}
                        </div>
                        <div className="mb-4">
                            <label class="uppercase tracking-wide text-gray-700 text-xs font-bold mb-2 flex justify-start" for="grid-first-name">
                                RG
                            </label>  
                            <InputMask className="appearance-none block w-full bg-gray-100 text-gray-700 border border-cyan-500 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"  {...register('rg')} id="outlined-basic" label="RG" variant="outlined" fullWidth value={rg} onChange={(e)=>setRg(e.target.value)}/>
                            {errors.rg?.message && <p>{errors.rg?.message}</p>}
                        </div>
                        <div className="mb-4">
                            <label class="uppercase tracking-wide text-gray-700 text-xs font-bold mb-2 flex justify-start" for="grid-first-name">
                                Nome da Mãe
                            </label>               
                            <TextField  {...register('nome_mae')}  id="outlined-basic" label="Nome da Mãe"variant="outlined" fullWidth value={nome_mae} onChange={(e)=>setNomeMae(e.target.value)}/>
                            {errors.nome_mae?.message && <p>{errors.nome_mae?.message}</p>}
                        </div>
                        <div className="mb-4"> 
                            <label class="uppercase tracking-wide text-gray-700 text-xs font-bold mb-2 flex justify-start" for="grid-first-name">
                                Data de Cadastro
                            </label>
                            <TextField {...register('data_de_cadastro')} id="outlined-basic" label="Data de Cadastro"variant="outlined" fullWidth value={data_de_cadastro} onChange={(e)=>setDataCadastro(e.target.value)} /> 
                            {errors.data_de_cadastro?.message && <p>{errors.data_de_cadastro?.message}</p>}
                        </div>
                        <div className="mb-8">
                            <label class="uppercase tracking-wide text-gray-700 text-xs font-bold mb-2 flex justify-start" for="grid-first-name">
                                Data de Nascimento
                            </label>
                            <TextField {...register('data_de_nascimento')} id="outlined-basic" label="Data de Nascimento" variant="outlined" fullWidth value={data_de_nascimento} onChange={(e)=>setDataNascimento(e.target.value)} /> 
                            {errors.data_de_nascimento?.message && <p>{errors.data_de_nascimento?.message}</p>}
                        </div>
                        <div className="mb-12"> 
                            <button className="px-3 py-2 bg-cyan-700 hover:bg-cyan-900 rounded-md text-white text-md mr-2 font-se" onClick={handleSubmit(handleClick)}> Cadastrar </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    )
}
